mapper.select:
SELECT user_id, user_name, user_type
FROM members
WHERE user_id = #{userId} AND user_password = #{userPwd}

SELECT user_password
FROM members
WHERE user_id = #{userId} AND user_name = #{userName} AND user_phone = #{userPhone}

SELECT user_id, user_password, user_name, email_id, email_domain, user_phone
FROM members
WHERE user_id = #{userId}

mapper.update:
UPDATE members
SET user_password = #{userPassword}, user_name = #{userName}, email_id = #{emailId}, email_domain = #{emailDomain}, user_phone = #{userPhone}
WHERE user_id = #{userId}

mapper.select:
SELECT user_id, user_password, user_name ,email_id, email_domain, user_phone
FROM members
WHERE user_id = #{id}
